import "core-js/modules/es.array.push.js";
export default {
  name: 'Typer',
  data() {
    return {
      words: [],
      //字母数组push，pop的载体
      str: "Hello, Welcome to Xiaole's Space.",
      //str初始化
      letters: [],
      //str分解后的字母数组
      showcur: false
    };
  },
  mounted() {
    this.begin();
  },
  methods: {
    begin() {
      this.letters = this.str.split('');
      for (var i = 0; i < this.letters.length; i++) {
        setTimeout(this.write(i), i * 200);
      }
    },
    back() {
      let L = this.letters.length;
      for (let i = 0; i < L; i++) {
        setTimeout(this.wipe(i), i * 100);
      }
    },
    write(i) {
      return () => {
        let L = this.letters.length;
        this.words.push(this.letters[i]);
        let that = this;
        if (i == L - 1) {
          this.showcur = true;
          setTimeout(function () {
            that.showcur = false;
            that.back();
          }, 1000);
        }
      };
    },
    wipe(i) {
      return () => {
        this.words.pop(this.letters[i]);
        /*如果删除完毕，在300ms后开始输入*/
        if (this.words.length == 0) {
          //  this.order++;
          let that = this;
          setTimeout(function () {
            that.begin();
          }, 300);
        }
      };
    }
  }
};
